import { Component, OnInit } from '@angular/core';

@Component({
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit{

  ngOnInit(): void {
    this.MobileMenu('true');
    document.addEventListener("scroll", (e) => {this.ScrollWindow()});
  }

  constructor() {

  }


  protected ScrollWindow() {
    // console.log(window.scrollY);
    if(window.scrollY > 200) {
      this.MobileMenu('false');
    } else {
      this.MobileMenu('true');
    }
  }

  protected BackToTop() {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  }

  protected MobileMenu(state: string) {
    let top_btn = document.getElementById('to-top');

    if(state === 'true') {
      top_btn!.classList.add('fade');
    } else if (state === 'false') {
      top_btn!.classList.remove('fade');
    }
  }
}
<app-topnav (mobile_menu)="MobileMenu($event)"></app-topnav>

<div class="container">
    <p>home</p>
</div>

<div class="about-overview">
    <p>overview</p>
</div>

<div class="featured-news">
    <p>news</p>
</div>

<button (click)="BackToTop()" id="to-top"><i id="up-arrow" class="fa-solid fa-chevron-up fa-xl"></i></button>
