import { Component, OnInit } from '@angular/core';

@Component({
  templateUrl: './services.component.html',
  styleUrls: ['./services.component.scss']
})
export class ServicesComponent implements OnInit {
  ngOnInit(): void {
    document.addEventListener("scroll", (e) => {this.ScrollWindow()});
    this.ScrollWindow();
  }

  private ScrollWindow() {
    this.curr_scroll_pos = window.scrollY;
    let top_btn = document.getElementById('to-top');

    if(window.scrollY > 200) {      
      top_btn!.classList.remove('fade');
    } else {
      top_btn!.classList.add('fade');
    }
  }

  //Scroll back to top when clicked
  protected BackToTop() {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  }


  protected curr_scroll_pos = 0;

  //Fade back-to-top btn
  protected MobileMenu(state: string) {
    let top_btn = document.getElementById('to-top');
    let to_about = document.getElementById('scroll-indicator');
    // console.log(state);


    if(state === 'open' && this.curr_scroll_pos < 200) {
      to_about!.classList.add('fade');
    } else if (state === 'open' && this.curr_scroll_pos > 200) {
      top_btn!.classList.add('fade');
    } else if (state === 'close' && this.curr_scroll_pos < 200) {      
      to_about!.classList.remove('fade');
    } else {
      top_btn!.classList.remove('fade');
    }
  }

  //expand service details
  protected ExpandDetail(item: number) {
    if(item === 1) {
      let state = document.getElementById("vibang");
      state!.classList.toggle("open")
      document.getElementById("expand")!.classList.toggle("open");
      document.getElementById("detail-des-p")!.classList.toggle("open");
      if(state!.classList.contains("open")) {
        document.getElementById("expand-btn")!.innerHTML = "Thu gọn";
      } else {
        document.getElementById("expand-btn")!.innerHTML = "Đọc thêm";
      }
    } else if(item === 2) {
      let state = document.getElementById("tongdat");
      state!.classList.toggle("open")
      document.getElementById("expand1")!.classList.toggle("open");
      document.getElementById("detail-des-p1")!.classList.toggle("open");
      if(state!.classList.contains("open")) {
        document.getElementById("expand-btn1")!.innerHTML = "Thu gọn";
      } else {
        document.getElementById("expand-btn1")!.innerHTML = "Đọc thêm";
      }

    } else if(item === 3) {
      let state = document.getElementById("dkthihanh");
      state!.classList.toggle("open")
      document.getElementById("expand2")!.classList.toggle("open");
      document.getElementById("detail-des-p2")!.classList.toggle("open");
      if(state!.classList.contains("open")) {
        document.getElementById("expand-btn2")!.innerHTML = "Thu gọn";
      } else {
        document.getElementById("expand-btn2")!.innerHTML = "Đọc thêm";
      }

    } else if(item === 4) {
      let state = document.getElementById("thihanh");
      state!.classList.toggle("open")
      document.getElementById("expand3")!.classList.toggle("open");
      document.getElementById("detail-des-p3")!.classList.toggle("open");
      if(state!.classList.contains("open")) {
        document.getElementById("expand-btn3")!.innerHTML = "Thu gọn";
      } else {
        document.getElementById("expand-btn3")!.innerHTML = "Đọc thêm";
      }

    }
  }
}
