import { Component, OnInit } from '@angular/core';

@Component({
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit{
  ngOnInit(): void {
    this.MobileMenu('true');
    document.addEventListener("scroll", (e) => {this.ScrollWindow()});

    let cursor = document.getElementById("circle");
    document.addEventListener('mousemove', function(e) {
      let x = e.clientX;
      let y = e.clientY;

      cursor!.style.left = x + "px";
      cursor!.style.top = y + "px";
    });
  }

  constructor() {

  }


  protected curr_scroll_pos = 0;


  protected ScrollWindow() {
    this.curr_scroll_pos = window.scrollY;

    if(window.scrollY > 200) {
      this.MobileMenu('false');
    } else {
      this.MobileMenu('true');
    }
  }

  protected BackToTop() {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  }

  protected MobileMenu(state: string) {
    let top_btn = document.getElementById('to-top');

    if(state === 'true') {
      top_btn!.classList.add('fade');
    } else if (state === 'false') {
      top_btn!.classList.remove('fade');
    }
  }
}
<app-topnav (mobile_menu)="MobileMenu($event)" [scroll_pos]="curr_scroll_pos"></app-topnav>

<div class="quotes">
    <p>home</p>
</div>

<div class="about-overview">
    <p>overview</p>
</div>

<div class="featured-news">
    <p>news</p>
</div>

<button (click)="BackToTop()" id="to-top"><i id="up-arrow" class="fa-solid fa-chevron-up fa-xl"></i></button>
<div id="circle"></div>
