import {
    trigger,
    transition,
    style,
    query,
    group,
    animateChild,
    animate,
    keyframes
} from '@angular/animations'

export const fade = trigger('routeAnimations', [
    transition('* <=> *', [
        query(':enter, :leave', [
            style({
                position: 'absolute',
                left: 0,
                width: '100%',
                opacity: 0,
                transform: 'scale(0) translateY(100%)'
            }),
        ]),
        query(':enter', [
            animate('0.6s ease', 
                style({
                    opacity: 1,
                    transform: 'scale(1) translateY(0)'
                }),
            )
        ]),
    ]),
])
